package minefield;import mvc.AppFactory;import mvc.Command;import mvc.Model;import mvc.View;/* *   Hoc Can 3/15/2021: implemented MineFieldFactory( all methods but makeEditCommand) *   Paul Junver Soriano 3/21/2021: implemented makeEditCommand * */public class MineFieldFactory implements AppFactory {    private String[] commands;    @Override    public Model makeModel() {        commands = new String[] {"NW", "N", "NE", "S", "SE", "SW", "W", "S"};        return new MineField();    }    @Override    public View makeView(Model mod) {        return new MineFieldView(mod);    }    public String getTitle() {        return "Mine Field";    }    @Override    public String[] getHelp() {        return new String[] {"Click on direction buttons to move and the goal is to reach to the bottom right"};    }    @Override    public String about() {        return "Mine Field by Hoc Can Huynh, Paul Junver Soriano";    }    @Override    public String[] getEditCommands() {        return commands;    }    @Override    public Command makeEditCommand(Model model, String type) {        MineFieldCommand command = new MineFieldCommand(model);        command.heading = Heading.valueOf(type);        return command;    }}